css:
body {
           font-family: "Times New Roman", serif;
           font-size: 11px;
           line-height: 1.2;
           margin: 3px;
           padding: 5px;
           position: relative;
           counter-reset: page;
       }
       
       .header {
           margin-bottom: 10px;
       }
       
       .company-info {
           float: left;
           width: 75%;
           font-size: 10px;
           line-height: 1.2;
       }
       
       .company-info .vietnamese {
           font-style: italic;
           font-weight: normal;
       }
       
       .document-info {
           float: right;
           width: 20%;
           text-align: center;
           font-size: 9px;
           padding: 5px;
       }
       
       .document-number {
           font-weight: bold;
           margin-bottom: 5px;
           font-size: 11px;
       }
       
       .barcode {
           margin-top: 5px;
       }
       
       .qr-content {
           display: flex;
           flex-direction: column;
           align-items: center;
       }
       
       .title-section {
           text-align: center;
           margin-bottom: 10px;
           clear: both;
           padding-top: 10px;
       }
       
       .main-title {
           font-size: 18px;
           font-weight: bold;
           margin-bottom: 3px;
            color: #000;
       }
       
       .sub-title {
           font-size: 16px;
           font-weight: bold;
           color: #666;
           font-style: italic;
       }
       
       .form-info {
           margin-bottom: 10px;
           clear: both;
       }
       
       .info-row {
           margin-bottom: 4px;
           display: flex;
           align-items: center;
       }
       
       .info-label {
           font-weight: bold;
           min-width: 200px;
       }
       
       .info-label .vietnamese {
           font-style: italic;
           font-weight: normal;
       }
       
       .info-value {
           flex: 1;
           border-bottom: 1px solid #ccc;
           padding-bottom: 1px;
           min-height: 14px;
       }
       
       .info-value .vietnamese {
           font-style: italic;
           font-weight: normal;
       }
       
       .table-container {
           margin: 10px 0;
       }
       
       .data-table {
           width: 100%;
           border-collapse: collapse;
           border: 1px solid #000;
           font-size: 11px;
       }
       
       .data-table th {
           background-color: #f5f5f5;
           border: 1px solid #000;
           padding: 1px;
           text-align: center;
           font-weight: bold;
           vertical-align: middle;
           font-size: 10px;
             color: #000;
       }
       
       .data-table th .vietnamese {
           font-style: italic;
           font-weight: normal;
            color: #000;
       }
       
       .data-table td {
           border: 1px solid #000;
           padding: 1px; 
           text-align: center;
           vertical-align: middle;
       }
       
       .data-table td.text-left {
           text-align: left;
       }
       
       .totals-section {
           margin: 10px 0;
           display: flex;
           justify-content: space-between;
       }
       
       .totals-item {
           flex: 1;
           text-align: left;
       }
       
       .totals-text {
           font-weight: bold;
           font-size: 11px;
       }
       
       .totals-text .vietnamese {
           font-style: italic;
           font-weight: normal;
       }
       
       .footer {
           margin-top: 10px;
           page-break-inside: avoid;
       }
       
       .explanation-section {
           margin-bottom: 10px;
       }
       
       .explanation-label {
           font-weight: bold;
           margin-bottom: 5px;
           font-size: 11px;
       }
       
       .explanation-label .vietnamese {
           font-style: italic;
           font-weight: normal;
       }
       
       .explanation-lines {
           height: 40px;
           padding: 5px;
       }
       
       .explanation-line {
           border-bottom: 1px solid #ccc;
           margin-bottom: 8px;
           height: 15px;
       }
       
       .signature-section {
           display: flex;
           justify-content: space-between;
           text-align: center;
       }
       
       .signature-box {
           width: 30%;
           padding: 5px;
           height: 80px;
           display: flex;
           flex-direction: column;
           justify-content: space-between;
       }
       
       .signature-title {
           font-weight: bold;
           margin-bottom: 3px;
           font-size: 11px;
       }
       
       .signature-title-vn {
           font-style: italic;
           color: #666;
           font-size: 9px;
           margin-bottom: 15px;
       }
       
       .signature-space {
           flex: 1;
           margin: 5px 0;
       }
       
       .signature-role {
           font-weight: bold;
           margin-top: 15px;
           font-size: 11px;
       }
       
       .signature-role-vn {
           font-style: italic;
           color: #666;
           font-size: 9px;
       }
       
       .page-number {
           position: fixed;
           bottom: 15px;
           right: 15px;
           font-style: italic;
           color: #666;
           font-size: 9px;
       }
       
       @page {
           margin: 0.4cm 0.4cm 0.4cm 0.6cm;
           @bottom-right {
               content: "Page " counter(page) " of " counter(pages);
               font-family: "Times New Roman", serif;
               font-style: italic;
               color: #666;
               font-size: 9px;
               margin-bottom: 10px;
           }
       }
       
       .clearfix::after {
           content: "";
           display: table;
           clear: both;
       }
